sys_ux_client_script.script:
/**
  * Populate values 
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({
    api,
    helpers
}) {
    const arr = [];

    for (var i = 0; i < api.data.look_up_multiple_records_1.results.length; i++) {
        let myObj = {};
        myObj.id = api.data.look_up_multiple_records_1.results[i]._row_data.uniqueValue;
        myObj.label = api.data.look_up_multiple_records_1.results[i].room_name.value;
      arr.push(myObj);
    }
    return arr;
}
